import {
  BrowserRouter,
  Route,
  Switch,
  Redirect,
  useHistory,
  useLocation
} from "react-router-dom";
import Presentation from "./pages/Presentation";
import Knowledges from "./pages/Knowledges";
import Portfolio from "./pages/Portfolio";
import Contact from "./pages/Contact";
import { Bigprojects1, Bigprojects2, Bigprojects3 } from "./pages/Bigprojects";

const App = () => {
  const location = useLocation();
  console.log("location", location);

  const history = useHistory();
  console.log("history", history);

  useEffect(() => {
    const handleScrollToElement = e => {
      console.log(e.wheelDeltaY);
      console.log(window.location.toString());

      const url = window.location.origin + "/";

      const wheelRouter = (after, before) => {
        if (e.wheelDeltaY < 0) {
          setTimeout(() => {
            history.push(after);
          }, 500);
        } else if (e.wheelDeltaY > 0) {
          setTimeout(() => {
            history.push(before);
          }, 500);
        }
      };
      switch (window.location.href.toString()) {
        case url:
          if (e.wheelDeltaY < 0) {
            setTimeout(() => {
              history.push("project-1");
            }, 500);
          }
          break;
        case url + "project-1":
          wheelRouter("project-2", "");
          break;

        case url + "project-2":
          wheelRouter("project-3", "project-1");
          break;

        case url + "project-3":
          wheelRouter("project-4", "project-2");
          break;
        case url + "project-4":
          wheelRouter("contact", "project-3");
          break;

        case url + "contact":
          if (e.wheelDeltaY > 0) {
            setTimeout(() => {
              history.push("project-4");
            }, 500);
          }
          break;

        default:
          console.log("nothing");
      }
    };

    window.addEventListener("wheel", handleScrollToElement);
  }, [history]);

  return (
    <div className="container-fluides">
      <div className="row">
        <BrowserRouter>
          <Switch location={location} key={location.pathname}>
            <Route path="/" exact component={Presentation} />
            <Route path="/knowledges" exact component={Knowledges} />
            <Route path="/portfolio" exact component={Portfolio} />
            <Route path="/contact" exact component={Contact} />
            <Route path="/project1" exact component={Bigprojects1} />
            <Route path="/project2" exact component={Bigprojects2} />
            <Route path="/project3" exact component={Bigprojects3} />
            <Redirect to="/portfolio" />
          </Switch>
        </BrowserRouter>
      </div>
    </div>
  );
};

export default App;
