import React from "react";
import Profilphoto from "../images/photoprofil.webp";

import { NavLink } from "react-router-dom";

const Navigation = () => {
  let myNavigation =
    " col-xl-3 myNavigation text-center p-5 text-light d-flex flex-column align-middle";
  let navItem = "nav-item p-4  ";
  let navLinkStyle = "text-decoration-none text-light display-6 ";

  const iconR = reseauxS => {
    return (reseaux = "fab fa-" + reseauxS + " display-1 mx-3");
  };

  return (
    <div className={myNavigation}>
      <div className="profilNamePhoto">
        <img scr={Profilphoto} alt="photo" />
        <h1 className="display-3 fw-bold  ">
          MOUSSA <br />
          MAHAMOUD
        </h1>
        <p className=" display-6 ">Developpeur Web</p>
      </div>

      <div className="items">
        <ul className="nav d-flex flex-column list-group text-light ">
          <NavLink exact to="/" className={navLinkStyle}>
            <li className={navItem}>Acceuil</li>
          </NavLink>

          <NavLink exact to="/knowledges" className={navLinkStyle}>
            <li className={navItem}>Connaissances</li>
          </NavLink>

          <NavLink exact to="/portfolio" className={navLinkStyle}>
            <li className={navItem}>Portfolio</li>
          </NavLink>

          <NavLink exact to="/contact" className={navLinkStyle}>
            <li className={navItem}>Contact</li>
          </NavLink>
        </ul>
      </div>

      <div className="d-flex">
        <i className={iconR("facebook")}></i>
        <i className={iconR("github")}></i>
        <i className={iconR("linkedin")}></i>
      </div>
    </div>
  );
};

export default Navigation;
