import React, { useEffect } from "react";

const Btnbutton = () => {
  useEffect(() => {
    let button = document.querySelector("button");

    const myButton = () => {
      document.body.style.background = "red";
    };
    const myButtonLeave = () => {
      document.body.style.background = "";
    };

    button.addEventListener("mouseover", myButton);
    button.addEventListener("mouseleave", myButton === false);
  });

  return <button style={{ backgroundColor: "red" }}>SCROLL</button>;
};

export default Btnbutton;
